"""
In theory we should be scrubbing through it.
So we take 4 at a time
"""
import sys

def main():
    test = convert("")
    test.reverse()
    test_cache = []
    for _ in range(0,4):
        test_cache.append(test.pop())


    if (len(set(test_cache)) == len(test_cache)):
        print(4)
        sys.exit()

    print(find(test, test_cache, step=4))

def is_unique(test):
    if(len(set(test)) == len(test)):
        return True
    else: return False

def convert(s):
    converted = []
    converted[:0] = s
    return converted

def find(test, test_cache, step):
    for _ in range(0, len(test)):
        test_cache.pop(0)
        test_cache.append(test.pop())
        step += 1
        if is_unique(test_cache):
            print(step)
            sys.exit()
        
        

if __name__ == "__main__":
    main()